package six;

public class Six_Foreach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	   
	 
		String[] a = { "Java","Hello","Programming"};
		for (String i : a) {   //foreach 구문
			System.out.println(i);
		}
		int [] b = {1,2,3,4,5};
		for(int i : b) {      //foreach 구문
			System.out.print(i);
		}

	
	}
}
